mapper.update:
update primary_login_user
ACCOUNT=#{account,jdbcType=VARCHAR}, PASSWORD=#{password,jdbcType=VARCHAR}, PHONE=#{phone,jdbcType=VARCHAR}, EMAIL=#{email,jdbcType=VARCHAR}, QQ=#{qq,jdbcType=VARCHAR},
where ID = #{id}

update.foreach:
update `primary_login_user`
ACCOUNT=#{item.account,jdbcType=VARCHAR}, PASSWORD=#{item.password,jdbcType=VARCHAR}, PHONE=#{item.phone,jdbcType=VARCHAR}, EMAIL=#{item.email,jdbcType=VARCHAR}, QQ=#{item.qq,jdbcType=VARCHAR},
where ID = #{item.id}

mapper.select:
    select 
    
    from
	primary_login_user
    where
    	1 = 1 
	    
	    and ID=#{id,jdbcType=BIGINT} 
	    
	    
	    and ACCOUNT=#{account,jdbcType=VARCHAR} 
	    
	    
	    and PASSWORD=#{password,jdbcType=VARCHAR} 
	    
	    
	    and PHONE=#{phone,jdbcType=VARCHAR} 
	    
	    
	    and EMAIL=#{email,jdbcType=VARCHAR} 
	    
	    
	    and QQ=#{qq,jdbcType=VARCHAR} 
	    
	    
	    and CREATE_TIME=#{createTime,jdbcType=TIMESTAMP} 
	    
	    
	    and UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP}

    select 
    
    from
	primary_login_user
    where
    	1 = 1
    	
        and ID=#{id,jdbcType=BIGINT} 
      	
    	
        and ACCOUNT=#{account,jdbcType=VARCHAR} 
      	
    	
        and PASSWORD=#{password,jdbcType=VARCHAR} 
      	
    	
        and PHONE=#{phone,jdbcType=VARCHAR} 
      	
    	
        and EMAIL=#{email,jdbcType=VARCHAR} 
      	
    	
        and QQ=#{qq,jdbcType=VARCHAR} 
      	
    	
        and CREATE_TIME=#{createTime,jdbcType=TIMESTAMP} 
      	
    	
        and UPDATE_TIME=#{updateTime,jdbcType=TIMESTAMP} 
      	
    limit 1
